import React, { Component } from 'react';
import './App.css';
import queryString from 'query-string';

let defaultStyle = {
  color: '#fff'
};

class PlaylistCounter extends Component {
  render() {
    return (
      <div style={{...defaultStyle, width: "40%", display: 'inline-block'}}>
        <h2>{this.props.playlists.length} playlists</h2>
      </div>
    );
  }
}

class HoursCounter extends Component {
  render() {
    let allSongs = this.props.playlists.reduce((songs, eachPlaylist) => {
      return songs.concat(eachPlaylist.songs)
    }, [])
    let totalDuration = allSongs.reduce((sum, eachSong) => {
      return sum + eachSong.duration
    }, 0)
    return (
      <div style={{...defaultStyle, width: "40%", display: 'inline-block'}}>
        <h2>{Math.round(totalDuration/60)} hours</h2>
      </div>
    );
  }
}

class Filter extends Component {
  render() {
    return (
      <div style={defaultStyle}>
        <img/>
        <input type="text" onKeyUp={(event) =>{
          this.props.onTextChange(event.target.value)
        }}/>
      </div>
    );
  }
}

class Playlist extends Component {
  render() {
    let playlist = this.props.playlist
    return (
      <div style={{...defaultStyle, display: 'inline-block', height: '20vh', width:'20%', border: '1px red solid'}}>
        <div id="album" style={{position: 'relative', height: '100%', width: '100%', margin: 0, border: '1px blue solid', textAlign: 'center'}}>
          <img src={playlist.imageUrl} style={{position: 'absolute', width: '100%', height: '100%', left: '0' }}/>
          <h3 style={{position: 'absolute', width: '100%', top: '0', border: '1px purple solid'}}>{playlist.name}</h3>
          <h4 style={{position: 'absolute', width: '100%', bottom: '0', border: '1px purple solid'}}>{playlist.artist_name}</h4>
        </div>
      </div>
    );
  }
}
/*<ul> list for song of album
          {playlist.songs.map(song =>
            <li>{song.name}</li>
          )}
        </ul> */

class App extends Component {
  constructor() {
    super();
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;

    this.state = {
      serverData: {},
      filterString: '',
      accessToken: accessToken
    }
  }

  componentDidMount() {
    fetch('https://api.spotify.com/v1/me', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
     // console.log(data)
      this.setState({
      user: {
        name: data.display_name
      }
    })})  
  }

  UpdateAlbums(text){
    let modifiedText = text.replace(/[&\/\\#,+()$~%.'":*?<>{}]/g, '') 

    fetch('https://api.spotify.com/v1/search?q=album%3A' + modifiedText + '&type=album', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      try {
        this.setState({
          playlists: data.albums.items.map(item => {
          //  console.log(data.albums.items)
            return{
              name: item.name,
              artist_name: item.artists[0].name,
              imageUrl: item.images[0].url,
              songs: []
            }
          })
        })
      } catch (error) {
          console.log(error)
      }})
  }

  render() {
    let playlistToRender =
      this.state.user &&
      this.state.playlists
        ? this.state.playlists
        : []

    return (
      <div className="App">
        {this.state.user ?
        <div>
          <h1 style={{...defaultStyle, 'font-size': '54px'}}>
            {this.state.user.name}'s Playlists
          </h1>
          <PlaylistCounter playlists={playlistToRender}/>
          <HoursCounter playlists={playlistToRender}/>
          <Filter onTextChange={(text) => {
              this.setState({filterString: text})
              this.UpdateAlbums(text)
            }}/>
          {playlistToRender.map(playlist =>
            <Playlist playlist={playlist} />
          )}
        </div> : <button onClick={() => window.location = 'http://localhost:8888/login' }
          style={{padding: '20px', 'font-size': '50px', 'margin-top': '20px'}}>Sign in with Spotify</button>
        }
      </div>
    );
  }
}

export default App;
