import React, { Component } from 'react';
import './App.css';
import queryString from 'query-string';

class PlaylistCounter extends Component {
  render() {
    return (
      <div className="counter">
        <div>{this.props.playlists.length} playlists found</div>
      </div>
    );
  }
}

class Filter extends Component {
  render() {
    return (
      <div id="filter_wrapper">
        <input id="filter_input" placeholder="Search..." type="text" onKeyUp={(event) =>{
          this.props.onTextChange(event.target.value)
        }}/>
      </div>
    );
  }
}

class Album extends Component {
  render() {
    let album = this.props.playlist
    return (
      <div className='album'>
        <div id="album_wrapper">
          <img src={album.imageUrl} style={{position: 'absolute', width: '100%', height: '100%', left: '0'}}/>
          <div id="album_info">
            {album.type}<br/>
            <span>{album.name}</span><br/>
            By <span>{album.artist_name}</span><br/>
            Date <span>{album.date}</span><br/>
            <span>{album.total_tracks}</span> song(s)</div>
        </div>
      </div>
    );
  }
}
/*<ul> list for song of album
          {playlist.songs.map(song =>
            <li>{song.name}</li>
          )}
        </ul> */

class User extends Component{
  render(){
    let user = this.props.user

    return(
        <div id="user_wrapper">
          <div style={{}}>{user.name}</div>
          <img src={user.avatar}></img>
        </div>
    )
  }
}

class App extends Component {
  constructor() {
    super();
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;

    this.state = {
      serverData: {},
      filterString: '',
      accessToken: accessToken
    }
  }

  componentDidMount() {
    fetch('https://api.spotify.com/v1/me', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      console.log(data)
      this.setState({
      user: {
        name: data.display_name,
        avatar: data.images[0].url
      }
    })})

    fetch('https://api.spotify.com/v1/search?q=album%3Arock&type=album', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      try {
        this.setState({
          playlists: data.albums.items.map(item => {
          //  console.log(data.albums.items)
            return{
              name: item.name,
              artist_name: item.artists[0].name,
              imageUrl: item.images[0].url,
              songs: []
            }
          })
        })
      } catch (error) {
          console.log(error)
      }})
  }

  UpdateAlbums(text){
    let modifiedText = text.replace(/[&\/\\#,+()$~%.'":*?<>{}]/g, '') 

    fetch('https://api.spotify.com/v1/search?q=album%3A' + modifiedText + '&type=album', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      try {
        this.setState({
          playlists: data.albums.items.map(item => {
            //console.log(item.album_type)
            return{
              type: item.album_type,
              name: item.name,
              artist_name: item.artists[0].name,
              imageUrl: item.images[0].url,
              total_tracks: item.total_tracks,
              date: item.release_date,
              songs: []
            }
          })
        })
      } catch (error) {
          console.log(error)
      }})
  }

  render() {
    let playlistToRender =
      this.state.user &&
      this.state.playlists
        ? this.state.playlists
        : []

    return (
      <div className="App">
        {this.state.user ?
        <div>
          <div id='top_bar'>
            <User user={this.state.user}/>
            <Filter onTextChange={(text) => {
                this.setState({filterString: text})
                this.UpdateAlbums(text)
              }}/>
            <PlaylistCounter playlists={playlistToRender}/>
          </div>
            <div id="albums_wrapper" style={{}}>
              {playlistToRender.map(playlist =>
                <Album playlist={playlist}/>
              )}
          </div>
        </div> : <button onClick={() => window.location = 'http://localhost:8888/login' }
          style={{padding: '20px', 'font-size': '50px', 'margin-top': '20px'}}>Sign in with Spotify</button>
        }
      </div>
    );
  }
}

export default App;
