import React, { Component } from 'react';
import '../App.css';

class Tracks extends Component {
    componentDidMount(){
        this.tracks = this.props.tracks
    }

    getDuration(ms){
        var minutes = Math.floor(ms / 60000);
        var seconds = ((ms % 60000) / 1000).toFixed(0) - 1;
        return minutes + ":" + (seconds < 10 ? '0' : '') + seconds;
      }

    render(){
        this.tracks = this.props.tracks
        return(
            <div id='tracks'>
                <ul>Tracks:
                {this.tracks.map(song =>
                <li>
                  <div id="track_name_play">
                    <button onClick=
                      {() => this.setState({
                      current_song: song.track_name,
                      current_song_src: song.preview_url})}>
                    </button>
                    <div id="track_name">{song.track_name}</div>
                  </div>
                  <div id="track_duration">{this.getDuration(song.duration)}</div>
                </li>
              )}
                </ul>
            </div>
        )
    }
}

export default Tracks
