import React, { Component } from 'react';
import '../App.css';

class AlbumOverlay extends Component{
  componentDidMount() {
    this.albumHref = this.props.albumHref
    console.log(this.albumHref)

    fetch('https://api.spotify.com/v1/me', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      try{
        this.setState({
        user: {
          name: data.display_name,
          avatar: data.images[0].url,
          href: data.external_urls.spotify
        }})
      }
      catch(e){
        console.log(e)
      }
    })
  }
  render(){

    return(
      <div style={{position:"fixed", right: '0', left: '0', top:'10vh', bottom:'0', background: '#000', width: '100%', height: '100%'}}>
        <div style={{color: 'white'}}>asad</div>
      </div>
    )
  }
}

export default AlbumOverlay
