import React, { Component } from 'react';
import '../App.css';

class AlbumOverlay extends Component{
  constructor(){
    super()
    this.state = {

    }
  }
  componentDidMount() {
    this.albumHref = this.props.albumHref
    this.accessToken = this.props.accessToken
    console.log(this.albumHref)

    fetch(`https://api.spotify.com/v1/albums/${this.albumHref}`, {
      headers: {'Authorization': 'Bearer ' + this.accessToken}
    }).then(response => response.json())
    .then(data => {
      console.log(data)
      try{
        this.setState({
          album: data.map(item => {
            return{
              artist_url: item.artists[0].external_urls.spotify,
              artist_name: item.artists[0].name,
              album_url: item.external_urls.spotify,
              image_url: item.images[0].url,
              album_name: item.name,
              popularity: item.popularity,
              release_date: item.release_date,
              total_tracks: item.total_tracks
            }
          })
        })
      }
      catch(e){
        console.log(e)
      }
    })

    console.log(this.state.artist_name)
  }
  render(){

    return(
      <div style={{position:"fixed", right: '0', left: '0', top:'10vh', bottom:'0', background: '#000', width: '100%', height: '100%'}}>
        <div style={{color: 'white'}}>asad</div>
      </div>
    )
  }
}

export default AlbumOverlay
