import React, { Component } from 'react';
import './App.css';
import queryString from 'query-string';

function call(text){
  console.log('Open album page' + text.toString());
}

function compareValues(key, order_by){
  return function(a, b){
    if(!a.hasOwnProperty(key) || !b.hasOwnProperty(key)){
      //property doesnt exist
      console.log('choose a key for ')
      return 0
    }

    const varA = (typeof a[key] === 'string')  
              ? a[key].toUpperCase() : a[key]
    const varB = (typeof b[key] === 'string')  
              ? b[key].toUpperCase() : b[key]

    let comparison = 0;

    if(varA > varB){
      comparison = 1
    }else if(varA < varB){
      comparison = -1
    }

    return(
      (order_by === 'desc') ? (comparison * -1) : comparison
    )
  }
}

class AlbumCounter extends Component {
  render() {
    return (
      <div className="counter">
        <div>{this.props.album.length} album(s) found</div>
      </div>
    );
  }
}

class Filter extends Component {
  render() {
    return (
      <div id="filter_wrapper">
        <input id="filter_input" placeholder="Search..." type="text" onKeyUp={(event) =>{
          this.props.onTextChange(event.target.value)
        }}/>
      </div>
    );
  }
}

class User extends Component{
  render(){
    let user = this.props.user

    return(
        <div id="user_wrapper">
          <div>{user.name}</div>
          <a href={user.href} target='_blank' rel="noopener noreferrer">
            <img src={user.avatar} alt="user avatar"></img>
          </a>
        </div>
    )
  }
}

class OrderKey extends Component{
  render(){
    return(
        <select id='order_key' name={'order_key'} onChange={(selected) => this.props.onKeyChange(selected.target.value)}>
          <option value='name'>Album Name</option>
          <option value='artist_name'>Artist Name</option>
          <option value='date'>Release Date</option>
        </select>
    )
  }
}

class OrderBy extends Component{
  constructor(){
    super();
    this.state = {
      rotation: 0,
      value: ''
    }
  }

  changeSource(){
    let updatedRotation = this.state.rotation
    let updatedValue = this.state.value

    if(updatedRotation === 90) {
      updatedRotation = 0
      updatedValue = ''
    }
    else if (updatedRotation === -90) {
      updatedRotation = 90
      updatedValue = 'desc'
    }
    else if(updatedRotation === 0) {
      updatedRotation = -90
      updatedValue = 'asc'
    }

    this.state.rotation = updatedRotation
    this.state.value = updatedValue
  }

  render(){
    return(
      <img id="order_by" src={require('./icons/arrow.svg')} alt='asc/desc'
       style={{transform: `rotate(${this.state.rotation}deg)`}}
      onClick={() => {
        this.changeSource()

        this.props.onOrderByChange(this.state.value)
      }}></img>
    )
  }
}

class Album extends Component 
{
  constructor(){
    super();
    this.display_style = 'none'
  }
  render() 
  {
    let album = this.props.album

    return (
      <div className='album'>
        <div  id="album_wrapper">
          <img src={album.imageUrl} alt={album.name + ' album cover'}/>
          <div id="album_info">
            {album.type.charAt(0).toUpperCase() + album.type.slice(1)}<br/>
            <span>{album.name}</span><br/>
            By <span>{album.artist_name}</span><br/>
            Date <span>{album.date}</span><br/>
            <span>{album.total_tracks}</span> song(s)<br/>
            <button onClick={() => {this.display_style = "none"
             console.log(this.display_style.toString)}}>Try it</button>
          </div>
        </div>
        <div id="albums_wrapper" style={{position:"fixed",zIndex: '200', backgroundColor: 'black', right: '0', left: '0', top:'10vh', bottom:'0', display: this.display_style}}>
          <AlbumOverlay style={{}}/>
        </div>
      </div>

    )
  }
}


/*<ul> list for song of album
          {album.songs.map(song =>
            <li>{song.name}</li>
          )}
        </ul> */

class AlbumOverlay extends Component{
  render(){
    return(
      <div>
        <div style={{color: 'white'}}>asad</div>
      </div>
    )
  }


}
class App extends Component {
  constructor() {
    super();
    let parsed = queryString.parse(window.location.search);
    let accessToken = parsed.access_token;

    this.state = {
      serverData: {},
      filterString: 'rock',
      accessToken: accessToken,
      order_key: 'name',
      order_by: ''
    }
  }

  componentDidMount() {
    fetch('https://api.spotify.com/v1/me', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      try{
        this.setState({
        user: {
          name: data.display_name,
          avatar: data.images[0].url,
          href: data.external_urls.spotify
        }})
      }
      catch(e){
        console.log(e)
      }
    })

    this.updateAlbums('rock');
  }

  updateAlbums(){
    let modifiedText = this.state.filterString.replace(/[&\\#,+()$~%.'":*?<>{}]/g, '') 

    fetch('https://api.spotify.com/v1/search?q=album%3A' + modifiedText + '&type=album', {
      headers: {'Authorization': 'Bearer ' + this.state.accessToken}
    }).then(response => response.json())
    .then(data => {
      try {
        this.setState({
          albums: data.albums.items.map(item => {
            return{
              type: item.album_type,
              name: item.name,
              artist_name: item.artists[0].name,
              imageUrl: item.images[0].url,
              total_tracks: item.total_tracks,
              date: item.release_date,
              href: item.id
            }
          })
        })
      } catch (e) {
          console.log(e)
      }})
  }

  render() {
    let renderedAlbums =
      this.state.user &&
      this.state.albums ? 
        this.state.albums
        : [] 

    if(this.state.order_by !== '')
      renderedAlbums.sort(compareValues(this.state.order_key, this.state.order_by)) 

    return (
      <div className="App">
        {this.state.user ?
          <div>
            <div id='top_bar'>
              <User user={this.state.user}/>
              <Filter onTextChange={(text) => {
                  this.setState({filterString: text})
                  this.updateAlbums(text)
                }}/>
              <AlbumCounter album={renderedAlbums}/>
              <div id='order_wrapper'>
                <div>Order By: </div>
                <OrderKey  onKeyChange={(value) => {
                  this.setState({order_key: value})
                  }}/>
                <OrderBy onOrderByChange={(value) => {
                  this.setState({order_by: value})
                  if(value === '') this.updateAlbums(this.state.filterString)
                }}/>
              </div>
            </div>
              <div id="albums_wrapper">
                {renderedAlbums.map(renderedAlbum =>
                  <Album album={renderedAlbum}/>
                )}
            </div>
          </div> : 
          <button onClick={() => window.location = 'http://localhost:8888/login' }
            style={{padding: '20px', 'font-size': '50px', 'margin-top': '20px'}}>Sign in with Spotify</button>
        }
      </div>
    );
  }
}

export default App;
