import React, { Component } from 'react';
import '../App.css';

class AlbumOverlay extends Component{
  constructor(){
    super()
    this.state = {

    }
  }
  componentDidMount() {
    this.albumHref = this.props.albumHref
    this.accessToken = this.props.accessToken
    console.log(this.albumHref)

    fetch(`https://api.spotify.com/v1/albums/${this.albumHref}`, {
      headers: {'Authorization': 'Bearer ' + this.accessToken}
    }).then(response => response.json())
    .then(data => {
      console.log(data.artists[0].external_urls.spotify)
      try{
        this.setState({
          album: {
              artist_url: data.artists[0].external_urls.spotify,
              artist_name: data.artists[0].name,
              album_url: data.external_urls.spotify,
              image_url: data.images[0].url,
              album_name: data.name,
              popularity: data.popularity,
              release_date: data.release_date,
              total_tracks: data.total_tracks,
              tracks: data.tracks.items.map((item) =>
                             {return {duration: item.duration_ms, track_name: item.name, preview_url: item.preview_url}} )
            }
        })
      }
      catch(e){
        console.log(e)
      }
    })

    
  }
  render(){
    console.log(this.state.album.artist_name)
    return(
      <div style={{position:"fixed", right: '0', left: '0', top:'10vh', bottom:'0', background: '#000', width: '100%', height: '100%'}}>
        <div style={{color: 'white'}}>asad</div>
      </div>
    )
  }
}

export default AlbumOverlay
