import React, { Component } from 'react';
import '../App.css';

import AlbumOverlay from './AlbumOverlay.js'

class Album extends Component 
{
  constructor(){
    super();
    this.state = {
      overlay_style: 'none',
      album_style: 'block'
    }
  }
  render() 
  {
    let album = this.props.album

    return (
      <div className='album'>
        <div  id="album_wrapper" style={{display: this.state.album_style}}>
          <img src={album.imageUrl} alt={album.name + ' album cover'}/>
          <div id="album_info">
            {album.type.charAt(0).toUpperCase() + album.type.slice(1)}<br/>
            <span>{album.name}</span><br/>
            By <span>{album.artist_name}</span><br/>
            Date <span>{album.date}</span><br/>
            <span>{album.total_tracks}</span> song(s)<br/>
            <button onClick={() => 
              {this.setState ({overlay_style: 'block'})}}
              >Open Album</button>
          </div>
        </div>
        <div id="albums_wrapper" style={{position:"fixed",zIndex: '200', right: '0', left: '0', top:'10vh', bottom:'0', display: this.state.overlay_style}}>
          <div style={{position:'absolute',right: '0',height: '100%', width: '100%', color: '#000', border: '1px red solid',cursor: 'pointer', fontSize: '25px'}}>X</div>
          <AlbumOverlay/>
        </div>
      </div>
        
    )
    
  }
  
}
/*<ul> list for song of album
          {album.songs.map(song =>
            <li>{song.name}</li>
          )}
        </ul> */

export default Album
